import * as React from 'react'; 
import { withRouter } from "react-router"; 
 

//updateStatus
const DogDetail = (props) => {
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    let fetchApiPath= "doggos-list";
    if(location && location.state && location.state.userInfo){
        fetchApiPath+=location.state.userInfo.id;
    }
    //thi.props.match.params.userid
    const  [petdetailFetchResponse, petdetailFetchRequest] = useApi(fetchApiPath, null,PortalConstants.APIMETHODS.GET);
    const [petdetailData , setPetdetailData] =useState(null);
    const [showError, setShowError] = useState(false);
    useEffect(() => {
        if(petdetailData === null && !loading && !petdetailFetchResponse.isLoading && petdetailFetchResponse.hasError === false){
            setLoading(true);
            petlistsFetchRequest(null,PortalConstants.APIMETHODS.GET,fetchApiPath);
        }
    });

    useEffect(() => {
        setLoading(false);
        if(petlistsFetchResponse.data && !petlistsFetchResponse.error && !petlistsFetchResponse.isLoading && !petlistsFetchResponse.hasError){
          setPetlistData(petlistsFetchResponse.data);
        }
        else if(petlistsFetchResponse.error && !petlistsFetchResponse.data && !petlistsFetchResponse.isLoading){
          setShowError(true);
        }
      }, [petlistsFetchResponse]);
     

  
     //thi.props.match.params.userid

    return (
        <>
            Dog Details
        </>

    );
}
export default withRouter(DogDetail);
